AUDIO_SOURCE_DIR=..

# create an array of the base32 characters
array_of_base32_chars=($(printf "" | sed -re 's|(.)|\1\n|g'))
for char in ${array_of_base32_chars[@]}; do
    cp $AUDIO_SOURCE_DIR/$char.wav ./data;
    touch ./data;
    git add ./data;
    git commit -m "üñ•Ô∏è" --allow-empty; # allow empty is important: otherwise we'd silently truncate letters that occur twice in a row.
done

git bundle create the_ai_repository_of_knowledge HEAD
